{
    "query": "SELECT p1.id, p1.name, COUNT(p2.id)::INTEGER as referral_count, COALESCE(SUM(drh.points_earned), 0)::INTEGER as total_earned FROM profiles p1 LEFT JOIN profiles p2 ON p1.id = p2.referred_by LEFT JOIN daily_reward_history drh ON p1.id = drh.user_id AND drh.reward_type = 'referral_bonus' WHERE p1.referral_code IS NOT NULL GROUP BY p1.id, p1.name HAVING COUNT(p2.id) > 0 ORDER BY referral_count DESC, total_earned DESC LIMIT 10"
}
